SELECT a.report_date AS reportDate,
FROM (SELECT report_date,
FROM (SELECT report_date,
FROM report_daily rad LEFT JOIN (SELECT c.adaccount_info_id, c.id AS campaign_info_id, a.id AS adgroup_info_id, cr.id AS creative_info_id FROM campaign_info c, adgroup_info a, creative_info cr WHERE c.id = a.campaign_info_id AND a.id = cr.adgroup_info_id AND c.adaccount_info_id = #{request.adAccountId}
) t1 ON rad.adaccount_info_id = #{request.adAccountId} AND t1.adaccount_info_id = rad.adaccount_info_id AND t1.campaign_info_id = rad.campaign_info_id AND t1.adgroup_info_id = rad.adgroup_info_id AND t1.creative_info_id = rad.creative_info_id WHERE rad.report_date BETWEEN #{request.startDate} AND #{request.endDate} GROUP BY rad.report_date UNION SELECT report_date,
FROM report_conversion_daily rac LEFT JOIN (SELECT c.adaccount_info_id, c.id AS campaign_info_id, a.id AS adgroup_info_id, cr.id AS creative_info_id FROM campaign_info c, adgroup_info a, creative_info cr WHERE c.id = a.campaign_info_id AND a.id = cr.adgroup_info_id AND c.adaccount_info_id = #{request.adAccountId}
) t1 ON rac.adaccount_info_id = #{request.adAccountId} AND t1.adaccount_info_id = rac.adaccount_info_id AND t1.campaign_info_id = rac.campaign_info_id AND t1.adgroup_info_id = rac.adgroup_info_id AND t1.creative_info_id = rac.creative_info_id WHERE rac.report_date BETWEEN #{request.startDate} AND #{request.endDate} GROUP BY rac.report_date) inner_report GROUP BY report_date ) a GROUP BY report_date ORDER BY report_date ASC

SELECT a.report_date      AS reportDate,

FROM (SELECT report_date,

FROM (SELECT report_date,

FROM report_daily rad
LEFT JOIN
(SELECT c.adaccount_info_id, c.id AS campaign_info_id, a.id AS adgroup_info_id, cr.id AS creative_info_id
FROM campaign_info c, adgroup_info a, creative_info cr
WHERE c.id = a.campaign_info_id
AND a.id = cr.adgroup_info_id
AND c.adaccount_info_id = #{request.adAccountId}

    AND c.id = #{campaignId}


    AND a.id = #{campaignId}


    AND cr.id = #{creativeId}

) t1
ON rad.adaccount_info_id = #{request.adAccountId}
AND t1.adaccount_info_id = rad.adaccount_info_id
AND t1.campaign_info_id = rad.campaign_info_id
AND t1.adgroup_info_id = rad.adgroup_info_id
AND t1.creative_info_id = rad.creative_info_id
WHERE rad.report_date BETWEEN #{request.startDate} AND #{request.endDate}
GROUP BY rad.report_date
UNION
SELECT report_date,

FROM report_conversion_daily rac
LEFT JOIN
(SELECT c.adaccount_info_id, c.id AS campaign_info_id, a.id AS adgroup_info_id, cr.id AS creative_info_id
FROM campaign_info c, adgroup_info a, creative_info cr
WHERE c.id = a.campaign_info_id
AND a.id = cr.adgroup_info_id
AND c.adaccount_info_id = #{request.adAccountId}

    AND c.id = #{campaignId}


    AND a.id = #{campaignId}


    AND cr.id = #{creativeId}

) t1
ON rac.adaccount_info_id = #{request.adAccountId}
AND t1.adaccount_info_id = rac.adaccount_info_id
AND t1.campaign_info_id = rac.campaign_info_id
AND t1.adgroup_info_id = rac.adgroup_info_id
AND t1.creative_info_id = rac.creative_info_id
WHERE rac.report_date BETWEEN #{request.startDate} AND #{request.endDate}
GROUP BY rac.report_date) inner_report
GROUP BY report_date ) a
