mapper.update:
UPDATE campaign_info
SET status = 'LIVE'
WHERE start_date <= #{statDate}
AND end_date >= #{statDate}
AND status IN ('READY')

UPDATE adgroup_info a, adgroup_schedule b
SET a.status = 'LIVE'
WHERE a.id = b.adgroup_info_id
AND b.start_date <= #{statDate}
AND b.end_date >= #{statDate}
AND a.status IN ('READY')
