mapper.sql:
SELECT a.id AS id, a.name AS name, b.member_type AS memberType, (SELECT id FROM adaccount_info ai WHERE ai.business_account_info_id = a.id ORDER BY ai.id ASC LIMIT 0,1) AS adAccountId FROM business_account_info a, business_account_user_info b WHERE a.id = b.business_account_info_id AND b.user_no = #{loginUserNo}
AND a.config IN ('ON', 'OFF') AND b.`status` = 'Y' UNION SELECT c.business_account_info_id AS id, e.name AS name, d.member_type AS memberType, c.id AS adAccountId FROM adaccount_info c, adaccount_user_info d, business_account_info e WHERE c.id = d.adaccount_info_id AND c.business_account_info_id = e.id AND d.user_no = #{loginUserNo} AND d.member_type = 'MEMBER'
AND e.config IN ('ON', 'OFF') AND c.config IN ('ON', 'OFF') AND d.`status` = 'Y' AND e.id NOT IN (SELECT a.id FROM business_account_info a, business_account_user_info b WHERE a.id = b.business_account_info_id AND b.user_no = #{loginUserNo} AND a.config IN ('ON', 'OFF') AND b.`status` = 'Y')

mapper.insert:
UPDATE business_account_info a, company_info b
   SET b.bank_info_id = #{request.bankId},
       b.account_number = #{request.accountNumber},
       b.account_owner = #{request.accountOwner}
 WHERE a.company_info_id = b.id
  AND a.id = #{request.businessAccountId}
