(SELECT g1.id, g2.name AS type_name, g3.name AS goal_name FROM ad_type_goal_info g1, ad_type_info g2, ad_goal_info g3 WHERE g1.ad_type_info_id = g2.id AND g1.ad_goal_info_id = g3.id AND g1.del_yn = 'N' AND g2.del_yn = 'N' AND g3.del_yn = 'N'
#{adTypeName}
#{adGoalName}
) d,
